var Channels={reload:function(){if(Pages.current!=1){return}if(SimpleChat.isOnline()){Loader.spinner.add("loading/info")}Channels.info(SimpleChat.feed(Settings.getId(),"info",4),300)},info:function(b,a){if(b.hasOwnProperty("title")){$("#channel-title-text").text(b.title.text)}if(a!=300){Loader.spinner.remove("loading/info")}},feed:function(a){if(!a.hasOwnProperty("type")||a.id!==Settings.getId()){return}if(a.feed=="info"){Channels.info(a.data,a.status)}}};$(document).ready(function(){$("#page-header").append('<div id="channel-title"><div id="channel-title-text"></div></div>');$("#channel-buttons").append('<div class="dropdown"><a id="channel-settings" data-toggle="dropdown" href="#"></a></div>')});Pages.onInfo.push(Channels.reload);if(typeof ChatView!=="undefined"){ChatView.reload.connect(Channels.reload);ChatView.feed.connect(Channels.feed)};