Profile.Field.city=function(a,b){Profile.addRow(a,Utils.left(htmlspecialchars(Utils.simplified(b)),128))};Profile.Field.site=function(a,b){var d=Utils.left(htmlspecialchars(b.replace(/\s+/gi,"")),254);var c=d;if(d.indexOf("http://")==0){c=d.slice(7)}else{if(d.indexOf("https://")==0){c=d.slice(8)}else{d="http://"+d}}Profile.addRow(a,'<a href="'+d+'">'+c+"</a>")};Profile.Field.email=function(a,b){var c=Utils.left(htmlspecialchars(b.replace(/\s+/gi,"")),254);if(c.indexOf("mailto:")==0){c=c.slice(7)}Profile.addRow(a,'<a href="mailto:'+c+'">'+c+"</a>")};