var Channels={acl:-1,reload:function(){if(Pages.current!=1){return}if(SimpleChat.isOnline()){Loader.spinner.add("loading/info")}Channels.info(SimpleChat.feed(Settings.getId(),"info",4),300)},info:function(b,a){if(b.hasOwnProperty("title")){$("#channel-title-text").text(b.title.text)}if(a!=300){Loader.spinner.remove("loading/info")}},feed:function(a){if(!a.hasOwnProperty("type")||a.id!==Settings.getId()){return}if(a.feed=="info"){Channels.info(a.data,a.status)}else{if(a.feed=="acl"){Channels.online()}}},online:function(){Channels.acl=SimpleChat.match(Settings.getId(),SimpleChat.id());var a=$("#settings-dropdown");if(Channels.acl==7){if(!a.length){$("#channel-buttons").append('<div id="settings-dropdown" class="dropdown pull-right"><a id="channel-settings" data-toggle="dropdown" href="#"></a>'+Channels.menu()+"</div>")}}else{a.remove()}},menu:function(){return'<ul class="dropdown-menu" role="menu" aria-labelledby="dropdownMenu"><li><a href="#" data-tr="channels_title">'+Utils.tr("channels_title")+"</a></li></ul>"}};$(document).ready(function(){$("#page-header").append('<div id="channel-title"><div id="channel-title-text"></div></div>')});Pages.onInfo.push(Channels.reload);if(typeof ChatView!=="undefined"){ChatView.reload.connect(Channels.reload);ChatView.feed.connect(Channels.feed)}if(typeof SimpleChat!=="undefined"){SimpleChat.online.connect(Channels.online)};