Messages.days=[];Messages.day=function(a){if($("#day-"+a).length){return}var c='<div class="day" id="day-'+a+'"><div class="day-header"><div class="day-day"><a href="#">'+SimpleChat.day(a)+'</a></div></div><div class="day-body"></div></div>';if(Messages.days.indexOf(a)==-1){Messages.days.push(a);Messages.days.sort()}var b=Messages.days.indexOf(a);if(b==Messages.days.length-1){$("#Chat").append(c)}else{$("#day-"+Messages.days[b+1]).before(c)}var e=$("#day-"+a);e.data("state","open");$("#day-"+a+" .day-header a").on("click",function(d){d.preventDefault();if(e.data("state")=="open"){e.data("state","closed");$("#day-"+a+" .day-body").hide()}else{e.data("state","open");$("#day-"+a+" .day-body").show()}alignChat()})};Messages.addRawMessage=function(b,c,a){$("#day-"+a+" .day-body").append(b);Messages.add(c)};Messages.reload=function(){if(Pages.current!=0){return}$(".day").remove();Messages.days=[];alignChat()};