Profile.Field.city=function(a,b){Profile.addRow(a,Utils.left(htmlspecialchars(b),128))};Profile.Field.site=function(a,b){Profile.addRow(a,Utils.left(htmlspecialchars(b),128))};Profile.Field.email=function(a,b){var c=Utils.left(htmlspecialchars(b),254).replace(/\s+/gi,"");if(c.indexOf("mailto:")==0){c=c.slice(7)}Profile.addRow(a,'<a href="mailto:'+c+'">'+c+"</a>")};